import React from 'react';
import Modal from 'react-modal';
import { useState } from 'react';


const BookModal = (props) => {

    const [book, setbook] = useState();
    return (
        <div>
            <Modal
                isOpen={props.isOpen}
                onRequestClose={props.closeModal}
                contentLabel="Book Modal"
            >
                <form>
                    <div>
                        <h1> Add Book </h1>
                        <div class="mb-3">
                            <label for="exampleFormControlInput1" class="form-label">Book Name</label>
                            <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Enter a book name in here" />
                        </div>

                        <div class="mb-3">
                            <label for="exampleFormControlInput1" class="form-label">Image</label>
                            <input type="text" class="form-control" id="exampleFormControlInput1" placeholder="Import an image" />
                        </div>

                        <div class="mb-3">
                            <label for="exampleFormControlTextarea1" class="form-label">Description</label>
                            <textarea class="form-control" id="exampleFormControlTextarea1" rows="3"></textarea>
                        </div>

                    </div>

                </form>
            </Modal>
        </div>
    )

}

export default BookModal;
