import { BookWrapper, NameInItem } from './styles';
import DeleteButton from "./buttons/DeleteButtons";
import { Link } from 'react-router-dom';


const BookItem = (props) => {


    return (
        <BookWrapper>
            <div className="container">
                <Link to={`/Books/${props.book.name}`}>
                    <img className="image-style" alt={props.book.name} src={props.book.image} />
                </Link>
                <div className="overlay">
                    <div className="text">{props.book.name}</div>
                </div>
            </div>
            <DeleteButton bookname={props.book.name} setQuery={props.setQuery} deletebook={props.deletebook} />
        </BookWrapper>

    );

};

export default BookItem;
