import Books from "../../Books";
import { makeAutoObservable } from 'mobx';

class BookStore {

    Books = Books;
    isLoading = false;

    constructor() {
        makeAutoObservable(this);
    }

    BookDelete = (book) => {
        const updateBooks = this.Books.filter((Book) => Book.name !== book);
        return this.Books = updateBooks;
    };



    getBooks = () => {

        this.Books = Books;
    }

}

const bookStore = new BookStore();

bookStore.getBooks();

export default bookStore;
