import { Link, useParams } from "react-router-dom";
import { observer } from "mobx-react";
import BookList from "./BookList";
import bookStore from "./stores/BookStore";
import shopStore from "./stores/shopStore";

const ShopItem = (props) => {
    const { shopSlug } = useParams();
    const shop = shopStore.shops.find((shop) => shop.slug === shopSlug)
    const books = shop.Books.map((book) => bookStore.getBookById(book.id));

    return (
        <>
            <div className="item">
                <Link to={`/shops/${props.shop.slug}`}>
                    <img src={props.shop.image} alt={props.shop.name} />
                </Link>
                <h3>{props.shop.name}</h3>
            </div>
            <BookList books={books} />
        </>
    );
};

export default observer(ShopItem);
