import './App.css';
import BookList from './components/BookList';
import { styled } from "styled-components";
// import Home from "./components/Home";
import { useState } from "react";

const theme = {
  light: {
    mainColor: "#293241",
    background: "#e0fbfc",
  },
  dark: {
    mainColor: "#e0fbfc",
    background: "#293241",
  },
};


// const Title = styled.h1`
