import React from 'react';
import { BookWrapper } from './styles';

const BookDetail = (props) => {
    const book = props.book;

    return (
        <BookWrapper>
            <h1> Books Detail </h1>
            <p>{book.name}</p>
            <img src={book.image} alt={book.name} />
            <p>{book.brief}</p>
        </BookWrapper>
    );

};

export default BookDetail;
