import { BookWrapper } from './styles';


const BookItem = (props) => {
    return (
        <BookWrapper>
            <img alt={props.book.name} src={props.book.image}
                onClick={() => props.setbook}
            />
            <p>{props.book.name}</p>
            {/* <p>{props.book.brief}</p> */}
        </BookWrapper>

    );

};

export default BookItem;
