import { useState } from "react";
import Books from "../Books";
import Booksitem from "./BookItem";
import SearchBar from "./SearchBar";
import bookStore from "./stores/BookStore";

const BookList = (bookStore) => {
    const [query, setQuery] = useState(null);


    const filteredBooks = bookStore.filter(book => book.name.toLowerCase().includes(query)).map((book) => (
        <Booksitem book={book} setBook={bookStore.setBook} BookDelete={bookStore} />
    ));
    return (
        <div>
            <SearchBar setQuery={setQuery} />
            <div className="list">{filteredBooks}</div>
        </div>

    );
};

export default BookList;
